def alias_unit(value):
    return value

def invalid_unit(value):
    return -9999.0

def lb_to_kg(value):
    return value / 2.20462

def kg_to_lb(value):
    return value * 2.20462

def c_to_f(value):
    return (value * 9.0 / 5.0) + 32.0

def f_to_c(value):
    return (value - 32.0) * 5.0 / 9.0

def div10(value):
    return value / 10

def div100(value):
    return value / 100

def div10k(value):
    return value / 10000

def div1k(value):
    return value / 1e3

def div1m(value):
    return value / 1e6

def times100(value):
    return value * 100

def times100(value):
    return value * 100

def times1000(value):
    return value * 1000

def times10k(value):
    return value * 10000

def times1ml(value):
    return value * 1000000

def times10ml(value):
    return value * 10000000